import React from 'react'
import ReactDOM from 'react-dom'
import './index.css'
import App from './App'
import reportWebVitals from './reportWebVitals'

let randomWord = require('random-words')
let wordle = getRandomNLetterWordle(7, randomWord.wordList).toUpperCase()

function randInt(lessThan) {
  return Math.floor(Math.random() * lessThan);
}

function getRandomNLetterWordle(length, wordList) {
  let lengthWords = wordList.filter(word => word.length === length)

  return lengthWords[randInt(lengthWords.length)]; 
}

ReactDOM.render(
  <React.StrictMode>
    <App rows={10} columns={100} wordle={wordle}/>
  </React.StrictMode>,
  document.getElementById('root')
);

// If you want to start measuring performance in your app, pass a function
