import { useState, useEffect } from 'react'
import Header from './components/Header.jsx'
import Game from './components/Game.jsx'


function getRandomNLetterWordle(length, wordList) {
  let rightSize = false;
  let wordUsed;
  while (!rightSize) {  
    wordUsed = wordList[randInt(wordList.length)];
    if(wordUsed.length === length) {
      rightSize = true;
    }

  }
  return wordUsed; 
}


function App() {
  const rows = 7
  const columns = 7
  const [userInput, setUserInput] = useState({key: '', time: 0})
  let wordList = require('random-words').wordList
  let wordle

  const pressKey = (event) => {
    setUserInput({key: event.key, time: event.timeStamp})
  } 

  const clickKey = (event) => {
    setUserInput({key: event.target.innerText, time: event.timeStamp})
  }

  useEffect(() => {
    window.addEventListener('keydown', pressKey)
    wordle = getRandomNLetterWordle(rows)
    console.log(wordle)

    return () => {
        window.removeEventListener('keydown', pressKey)
    }
  }, [])

  return (
    <div className='App'>
      <Header/>
      <Game input={userInput} 
            rowLength={rows} 
            colLength={columns} 
            wordle={wordle}
            handleKeyClick={clickKey}
      />
    </div>
  )
}

export default App
