import React from 'react'
import {Link} from "react-router-dom";
import '../../css/HomePage.css';
import Box from '../Box.jsx'

const getColorFromLetter = (color) => {
  if (color === 'g') {
    return 'green'
  } else if (color === 'y') {
    return 'yellow'
  } else {
    return 'grey'
  }
}


const getRowFormat = (word, color) => {
  let wordArray = word.split('')

  return wordArray.map((letter, i) => ({letter: letter, color: getColorFromLetter(color[i])}))
}


