import React from 'react'
import Box from './Box.jsx';



const Timer = () => {
    const time = useRef(60)
    let timesRun = 0;
    let interval = setInterval(function(){
        timesRun += 1;
        if(timesRun === 60){
            clearInterval(interval);
        }
        //do whatever here..
    }, 2000); 


    return (
        <div className="timer">
            <Box color='' letter={Math.floor(time.current)}/>
            <Box color='' letter={time.current % 10}/>
        </div>
    )
}

export default Timer
