import React from 'react'
import {Link} from "react-router-dom";
import '../../css/HomePage.css';
import Row from '../Row.jsx'

const getColorFromLetter = (color) => {
  if (color === 'G') {
    return 'green'
  } else if (color === 'Y') {
    return 'yellow'
  } else if (color === 'X') {
    return 'gray'
  } else {
    return ''
  }
}

const formatToRow = (word, color) => {
  let wordArray = word.toUpperCase().split('')
  let c = color.toUpperCase()
  return wordArray.map((letter, i) => ({letter: letter, color: getColorFromLetter(c[i])}))
}


const HomePage = ({socket}) => {
  console.log()


  return (
    <div className='HomePage' onClick={() => console.log("CLICKED")}>
      <h1> HOMEPAGE </h1>


      <div className='CreateRoom'>
        <Row row={formatToRow("CREATE", "ggyyzz")}/>
        <Row row={formatToRow("ROOM  ", "gxyyzz")}/>

      </div>

      <Link to='/lobby'> <button id='createRoomButton'>Create Private Room</button> </Link>


    </div>

  )
}

export default HomePage
