import {Link} from "react-router-dom";
import '../../css/LobbyPage.css';

const LobbyPage = ({socket, onSettingChange}) => {

    let setting = {rows: 3, cols: 7, wordle: 'GRAHAMS', wordList: []}

    return (
        <div>

            <div className="settings">

                <h> Settings </h>

                <div>
                    <label for="rows">Number of Attempts(Rows):</label>
                    <select name="rows" id="rowSelect">
                        {Array.from({length: 12}, (_,i) => {return <option> {i + 5} </option>})}
                    </select>
                </div>

                <div>
                    <label for="columns">Number of Letters(Columns):</label>
                    <select name="columns" id="columnSelect">
                        {Array.from({length: 12}, (_,i) => {return <option> {i + 5} </option>})}
                    </select>
                    </div>


            </div>






            <Link to='/game' onClick={() => onSettingChange(setting)}> GO TO GAME </Link>

        </div>
    )
}

export default LobbyPage
