import {Link} from "react-router-dom";
import '../../css/LobbyPage.css';
import {DropdownButton, Dropdown} from "react-bootstrap"



const LobbyPage = ({socket, onSettingChange}) => {

    let setting = {rows: 3, cols: 7, wordle: 'GRAHAMS', wordList: []}

    return (
        <div>

            <div className="settingsContainer">
                sdaf

            </div>


            Game Settings


            <div className='settings'>

                Number of Attempts(Rows)
                <form>
                    <select className="settingsForm" name="rows">
                        {Array.from({length: 12}, (v, i) => <option value="asdf"> {i + 5}</option>)}

                    </select>
                </form>
                
                Word Length(Columns)
                <form>
                    <select className="settingsForm" name="columns">
                        {Array.from({length: 12}, (v, i) => <option value="asdf"> {i + 5}</option>)}

                    </select>
                </form>

                
            </div>

            <Link to='/game' onClick={() => onSettingChange(setting)}> GO TO GAME </Link>

        </div>
    )
}

export default LobbyPage
