import {React, useRef, useState} from 'react'
import Box from './Box.jsx';



const Timer = () => {
    const [time, setTime] = useState(60)

    let timesRun = 0;
    let interval = setInterval(function(){
        timesRun += 1;
        if(timesRun === 60){
            clearInterval(interval);
        }

        setTime(time + 1)
    }, 1000); 


    return (
        <div className="timer">
            <Box color='' letter={Math.floor(time.current / 10)}/>
            <Box color='' letter={time.current % 10}/>
        </div>
    )
}

export default Timer
