import {React, useEffect, useState} from 'react'
import Box from './Box.jsx';



const Timer = () => {
    const [time, setTime] = useState(60)

    setInterval(function(){
        if (time % 3 === 0) {
            console.log(time)
        }

        setTime(time => time - 1)
    }, 1000)
    

    return (
        <div className="timer">
            <Box color='' letter={Math.floor(time / 10)}/>
            <Box color='' letter={time % 10}/>
        </div>
    )
}

export default Timer
