import {React, useEffect, useState, useRef} from 'react'
import Box from './Box.jsx';

const Timer = ({maxTime, socket, currentPlayer, getNextPlayer, changeTyping}) => {
    const [seconds, setSeconds] = useState(maxTime)
    const inGame = useRef(true)

    useEffect(() => {
        socket.on('resetTimer', () => {
            setSeconds(maxTime)
        })

        socket.on('endGame', () => {
            inGame.current = false
        })

    }, [])

    useEffect(() => {
        if (!inGame.current) {
            return
        }

        const timer = setTimeout(function() {
            if (seconds === 0 && inGame.current) {
                changeTyping(false)

                setTimeout(() => {
                    if (currentPlayer.id === socket.id) {
                        console.log("NEXT PLAYER BY TIMER")
                        getNextPlayer()
                    }
                }, 1000)

                return
            }

            if (seconds > 0) {
                setSeconds(seconds => seconds - 1)
            }
        }, 1000); 

        return () => {
            window.clearTimeout(timer);
        }
    }, [seconds])

    return (
        <div className="timer">
            <Box color='' letter={Math.floor(seconds / 10)}/>
            <Box color='' letter={seconds % 10}/>
        </div>
    )
}

export default Timer
