import React from 'react'
import {useState} from 'react'
import {Link} from "react-router-dom";
import '../../css/HomePage.css';
import Box from '../Box.jsx'

const getColorFromLetter = (color) => {
  if (color === 'G') {
    return 'green'
  } else if (color === 'Y') {
    return 'yellow'
  } else if (color === 'X') {
    return 'gray'
  } else {
    return ''
  }
}

const createRow = (word, color, name) => {
  let wordArray = word.toUpperCase().split('')
  let c = color.toUpperCase()
  return wordArray.map((letter, i) => (<Box className={name} letter={letter} color={getColorFromLetter}/>))
}

const HomePage = ({socket}) => {

  const [highlight, setHighlight] = useState("zzzzzz")


  return (
    <div className='HomePage' onClick={() => console.log("CLICKED")}>
      <Link className='toCreateLobby' to='/lobby'>
        {
          createRow("CREATE", "gggggg", "create")


        }


      </Link>

      <form className='input'>
        <input className='inputBar' type="text" name="name" />


      </form>

    </div>

  )
}

export default HomePage
